import React from 'react';
import speakerSvg from '../IMG/music-player.svg'
import useSound from 'use-sound';
import sound1 from '../SOUNDS/sound1.mp3';

function MusicComponent() {

  const soundUrl = '../SOUNDS/sound1.mp3';
  const [play, { stop, isPlaying }] = useSound(soundUrl);

  return (
    <div className="music-component">
      <h1 className="music-header">SUPERIOR SOUND</h1>
      <p className="music-subtext">Experience live versions of your favorite songs.</p>
      <button className="demo-button">SEE DEMO</button>
      <div className="audio-container">
        <img src={speakerSvg} alt="speakerSvg"/>
        <button 
          className="play-music-button" 
          active={isPlaying}
          size={60}
          iconColor="var(--color-background)"
          idleBackgroundColor="var(--color-text)"
          activeBackgroundColor="var(--color-primary)"
          play={play}
          stop={stop}>CLICK TO PLAY</button>
        </div>
    </div>
  )
}

export default MusicComponent;
